import React, {useState, useEffect, useContext} from "react"

function PlayerForm(props) {

    const [wagers, setWagers] = useState([
        {
            username: "JokerrNikita",
            game_type: "Chess",
            website: "Chess.com",
            description: "1 game, 3 minute + 1 second addon.",
            wager_value: 5,
            currency: "USD",
            isOnline: true,
            isCompleted: false
        },
    ])

    function toggleTodoCompleteAtIndex(index) {
        const temporaryWagers = [...wagers];
        temporaryWagers[index].isCompleted = !temporaryWagers[index].isCompleted;
        setWagers(temporaryWagers);
    }

    return (
        <div className="formApp">
            <form className="wager-list">
                <ul className='form-ul-wager-list'>
                        {wagers.map((wager, index) => (
                            <div className={`todo ${wager.isCompleted && 'todo-is-completed'}`}>
                                <div className={'checkbox'} onClick={() => toggleTodoCompleteAtIndex(index)}>
                                {wager.isCompleted && (<span>&#x2714;</span>)}
                                </div>
                                <li>
                                    <label>Your in-game username</label>
                                    <input
                                    type="text"
                                    value={wager.username}
                                    name="username"
                                    />
                                </li>
                                <li>
                                    <label>Choose game genre</label>
                                    <select>
                                        <option value="">--Please choose an option--</option>
                                        <option value="chess">Chess</option>
                                    </select>
                                </li>
                                <li>
                                    <label>Website of the game</label>
                                    <input
                                    type="text"
                                    value={wager.website}
                                    name="username"
                                    />
                                </li>
                                <li>
                                    <label>Description of the wager</label>
                                    <input
                                    type="text"
                                    value={wager.description}
                                    name="description"
                                    />
                                </li>
                                <li>
                                    <label>Wager value (how much you need to bet. The owner of the challenge already deposited money for it.)</label>
                                    <input
                                    type="text"
                                    value={wager.wager_value}
                                    name="wager_value"
                                    />
                                </li>
                                <li>
                                    <label>Currency</label>
                                    <input
                                    type="text"
                                    value={wager.currency}
                                    name="currency"
                                    />
                                </li>
                                <li>
                                    <label>Description of the wager</label>
                                    <input
                                    type="text"
                                    value={wager.description}
                                    name="description"
                                    />
                                </li>
                            </div>
                        ))}
                </ul>
            </form>
            <div className={`todos`}>
            <li>
                <label>Your in-game username</label>
                <input
                type="text"
                value={wager.username}
                name="username"
                />
            </li>
            <li>
                <label>Choose game genre</label>
                <select>
                    <option value="">--Please choose an option--</option>
                    <option value="chess">Chess</option>
                </select>
            </li>
            <li>
                <label>Website of the game</label>
                <input
                type="text"
                value={wager.website}
                name="username"
                />
            </li>
            <li>
                <label>Description of the wager</label>
                <input
                type="text"
                value={wager.description}
                name="description"
                />
            </li>
            <li>
                <label>Wager value (how much you need to bet. The owner of the challenge already deposited money for it.)</label>
                <input
                type="text"
                value={wager.wager_value}
                name="wager_value"
                />
            </li>
            <li>
                <label>Currency</label>
                <input
                type="text"
                value={wager.currency}
                name="currency"
                />
            </li>
            <li>
                <label>Description of the wager</label>
                <input
                type="text"
                value={wager.description}
                name="description"
                />
            </li>
            </div>
        </div>
    )

}




