import React, {useState, useEffect, useContext} from "react"

function PlayerForm(props) {

    const [newWager, setNewWagers] = useState({
        username: "Nikita",
        game_type: "Chess",
        website: "",
        description: "",
        wager_value: 0,
        currency: "USD"
    })
    const [wagers, setWagers] = useState([
        {
            username: "JokerrNikita",
            game_type: "Chess",
            website: "Chess.com",
            description: "1 game, 3 minute + 1 second addon.",
            wager_value: 5,
            currency: "USD"
        },
    ])

    function handleChange(e) {
        const {name, value} = e.target
        setNewWagers(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    function handleSubmit(e) {
        e.preventDefault();

        setWagers([newWager])
    }

    return (
        <div className="app">
            <form className="todo-list" onSubmit={handleSubmit}>
                <ul className='form-ul-wager-list'>
                    <div className={`todo`}>
                        <div className='div-element'>
                            <label>Your in-game username</label>
                            <input
                            type="text"
                            value={newWager.username}
                            name="username"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-element'>
                            <label>Choose game genre</label>
                            <input
                            type="text"
                            value={newWager.game_type}
                            name="game_type"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-element'>
                            <label>Website of the game</label>
                            <input
                            type="text"
                            value={newWager.website}
                            name="website"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-element'>
                            <label>Description of the wager</label>
                            <input
                            type="text"
                            value={newWager.description}
                            name="description"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-element'>
                            <label>Wager value (how much you need to bet. The owner of the challenge already deposited money for it.)</label>
                            <input
                            type="number"
                            value={newWager.wager_value}
                            name="wager_value"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-element'>
                            <label>Currency</label>
                            <input
                            type="text"
                            value={newWager.currency}
                            name="currency"
                            onChange={handleChange}
                            />
                        </div>
                        <div className='div-button'>
                            <button>
                                Add a Wager!
                            </button>
                        </div>
                    </div>
                </ul>
            </form>
            {wagers.map((wager, index) => (
                <div className="todo-list">
                    <div className={`todo`}>
                        <li key={wager.username}>
                            {wager.username}
                        </li>
                        <li key={wager.game_type}>
                            {wager.game_type}
                        </li>
                        <li key={wager.description}>
                            {wager.description}
                        </li>
                        <li key={wager.website}>
                            {wager.website}
                        </li>
                        <li key={wager.wager_value}>
                            {wager.wager_value}
                        </li>
                        <li key={wager.currency}>
                            {wager.currency}
                        </li>
                    </div>
                </div>
            ))}
        </div>
    )

}

export {PlayerForm}
