import React, {useState, useEffect, useContext} from "react"

function PlayerForm(props) {

    const [newWager, setNewWagers] = useState({
        username: "",
        game_type: "",
        website: "",
        description: "",
        wager_value: 0,
        currency: "USD",
        isOnline: true,
        isCompleted: false
    })
    const [wagers, setWagers] = useState([
        {
            username: "JokerrNikita",
            game_type: "Chess",
            website: "Chess.com",
            description: "1 game, 3 minute + 1 second addon.",
            wager_value: 5,
            currency: "USD",
            isOnline: true,
            isCompleted: false
        },
    ])

    function toggleTodoCompleteAtIndex(index) {
        const temporaryWagers = [...wagers];
        temporaryWagers[index].isCompleted = !temporaryWagers[index].isCompleted;
        setWagers(temporaryWagers);
    }

    function handleChange(e) {
        setNewWagers(
            // Computed property names
            // keys of the objects are computed dynamically
            ([e.target.name] : e.target.value)
        )
    }

    function handleSubmit(e) {
        e.preventDefault();

        const allWagers = [...wagers];
        allWagers.concat(newWager);
        setWagers(allWagers);

    }

    return (
        <div className="formApp">
            <form className="wager-list" onSubmit={handleSubmit}>
                <ul className='form-ul-wager-list'>
                    <div className={`todo`}>
                        <li>
                            <label>Your in-game username</label>
                            <input
                            type="text"
                            value={newWager.username}
                            name="username"
                            onChange={handleChange}
                            />
                        </li>
                        <li>
                            <label>Choose game genre</label>
                            <input
                            type="text"
                            value={newWager.game_type}
                            name="game_type"
                            onChange={handleChange}
                            />
                        </li>
                        <li>
                            <label>Website of the game</label>
                            <input
                            type="text"
                            value={newWager.website}
                            name="website"
                            />
                        </li>
                        <li>
                            <label>Description of the wager</label>
                            <input
                            type="text"
                            value={newWager.description}
                            name="description"
                            />
                        </li>
                        <li>
                            <label>Wager value (how much you need to bet. The owner of the challenge already deposited money for it.)</label>
                            <input
                            type="number"
                            value={newWager.wager_value}
                            name="wager_value"
                            />
                        </li>
                        <li>
                            <label>Currency</label>
                            <input
                            type="text"
                            value={newWager.currency}
                            name="currency"
                            />
                        </li>
                        <div>
                            <button>
                                Add a Wager!
                            </button>
                        </div>
                    </div>
                </ul>
            </form>
            {wagers.map((wager, index) => (
            <div className={`todo ${wager.isCompleted && 'todo-is-completed'}`}>
                <div className={'checkbox'} onClick={() => toggleTodoCompleteAtIndex(index)}>
                {wager.isCompleted && (<span>&#x2714;</span>)}
                </div>
                <li>
                    <label>Your in-game username</label>
                    <input
                    type="text"
                    value={wager.username}
                    name="username"
                    />
                </li>
                <li>
                    <label>Choose game genre</label>
                    <select>
                        <option value="">--Please choose an option--</option>
                        <option value="chess">Chess</option>
                    </select>
                </li>
                <li>
                    <label>Website of the game</label>
                    <input
                    type="text"
                    value={wager.website}
                    name="username"
                    />
                </li>
                <li>
                    <label>Description of the wager</label>
                    <input
                    type="text"
                    value={wager.description}
                    name="description"
                    />
                </li>
                <li>
                    <label>Wager value (how much you need to bet. The owner of the challenge already deposited money for it.)</label>
                    <input
                    type="text"
                    value={wager.wager_value}
                    name="wager_value"
                    />
                </li>
                <li>
                    <label>Currency</label>
                    <input
                    type="text"
                    value={wager.currency}
                    name="currency"
                    />
                </li>
                <li>
                    <label>Description of the wager</label>
                    <input
                    type="text"
                    value={wager.description}
                    name="description"
                    />
                </li>
            </div>
        ))}
        </div>
    )

}




